import {ShopActionsType} from './shop.types';
import {takeEvery,call,put} from 'redux-saga/effects';

import {firestore,convertCollectionSnapChotToMap} from '../../firebase/firebase.utils';

import {fetchCollectionsFail,fetchCollectionsSucess} from './shop.action';

export function* fetchCollectionAsync(){
    yield console.log('I am fired');

    try{
    const collectionRef =  firestore.collection('collections');
    const snapchot = yield collectionRef.get();

    const collectionMap = yield call(convertCollectionSnapChotToMap,snapchot);

    put(fetchCollectionsSucess(collectionMap))
    }catch(error){
        put(fetchCollectionsFail(error.message));
    }


        // collectionRef.get().then( async (snapchot)=>{
        //    const collectionMap = convertCollectionSnapChotToMap(snapchot);   
        //    dispach(fetchCollectionsSucess(collectionMap))

        // }).catch(error=>{
        //     dispach(fetchCollectionsFail(error.message));
        // })
}

export function* fetchCollectionsStart(){
    yield takeEvery(ShopActionsType.FETCH_COLLECTIONS_START,
         fetchCollectionAsync
         );
}
