import React from 'react';
import {Route} from 'react-router-dom';
import {connect} from 'react-redux';
import {createStructuredSelector} from 'reselect';
import {fetchCollectionsStartAsync} from '../../redux/shop/shop.action';
import {selectIsFetching,selectIsCollectionIsLoaded} from '../../redux/shop/shop.selector'


import CollectionsOverviewComponent from '../../component/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';
import WithSpinner from '../../component/with-spinner/with-spinner.component';

const CollectionOverViewWithSpinner = WithSpinner(CollectionsOverviewComponent);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component{ 



    componentDidMount(){
        const {fetchCollectionsStartAsync} = this.props;
        fetchCollectionsStartAsync();

    }

    render(){ 
        const {match,isCollectionFetching,isCollectionLoaded} = this.props;
        console.log(isCollectionFetching);
        return(

        <div className='shop-page'>
            <Route exact path={`${match.path}`} render={ (props)=> <CollectionOverViewWithSpinner isLoading={isCollectionFetching} {...props} /> }/>
            <Route path={`${match.path}/:collectionId`} render={ (props)=> <CollectionPageWithSpinner isLoading={!isCollectionLoaded} {...props}/> }/>
        </div>
        )
    }
}

const mapStateToProps = createStructuredSelector({
    isCollectionFetching: selectIsFetching,
    isCollectionLoaded : selectIsCollectionIsLoaded
})


const mapDispatchToProps = dispatch => ({
    fetchCollectionsStartAsync : () => dispatch(fetchCollectionsStartAsync()) 
})


export default connect(mapStateToProps,mapDispatchToProps)(ShopPage);
