import {takeLatest,put,all,call} from 'redux-saga/effects';

import {UserActionTypes} from './user.types';
import {googleSignInFail,googleSigninSucess} from './user.actions'

import {auth,googleAuthProvider,createUserprofileDocument} from '../../firebase/firebase.utils';


export function* signInwithGoogle(){
    try {
        const {user} =  yield auth.signInWithPopup(googleAuthProvider);
        const userRef = yield call(createUserprofileDocument,user);
        const userSnapchot = yield userRef.get();

        yield put(googleSigninSucess({id:userSnapchot.id, ...userSnapchot.data()}));
    } catch (error) {
        yield put(googleSignInFail(error));
    }


}

export function* onGooogleSignInStart(){
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START,signInwithGoogle)
}

export function* onEmailSignInStart(){
    yield t
}

export function* userSagas(){
    yield all([call(onGooogleSignInStart)])
}
