import SHOP_DATA from './shop.data';
import {ShopActionsType} from './shop.types'

const INITIAL_VALUE = {
collections: SHOP_DATA
}

  const shopReducer = (state= INITIAL_VALUE,action)=>{
      switch(action.type){
          case ShopActionsType.UPDATE_COLLECTION:
              return {
                  ...state,
                  collections
              }
          default:
              return state;
      }
  }

  export default shopReducer;
