import {createSelector} from 'reselect';

const COLLECTION_ID_MAP = {
    hats: 1,
    sneakers:2,
    jackets:3,

}

const selectShop = state => state.shop;

export const selectShopCollection = createSelector(
    [selectShop],
    (shop)=> shop.collection
)
