import React from 'react';
import {Route} from 'react-router-dom';
import {connect} from 'react-redux';


import CollectionsOverviewComponent from '../../component/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';
import {firestore,convertCollectionSnapChotToMap} from '../../firebase/firebase.utils';
import {updateCollections} from '../../redux/shop/shop.action';

class ShopPage extends React.Component{ 

    state = {
        loading:true
    }

    unSubscribeFromSnapChot = null;

    componentDidMount(){
        const { updateCollections} = this.props;
        const collectionRef = firestore.collection('collections');

        collectionRef.onSnapshot( async (snapchot)=>{
           const collectionMap = convertCollectionSnapChotToMap(snapchot);   
           updateCollections(collectionMap);
           this.setState({});

        })
    }

    render(){ 
        const {match} = this.props;
        return(

        <div className='shop-page'>
            <Route exact path={`${match.path}`} component={CollectionsOverviewComponent} />
            <Route path={`${match.path}/:collectionId`} component={CollectionPage}/>
        </div>
        )
    }
}

const mapDispatchToProps = dispatch => ({
    updateCollections : collectionMap => dispatch(updateCollections(collectionMap)) 
})


export default connect(null,mapDispatchToProps)(ShopPage);
